Hi all,

There is typo in above code. It should be corrected as below,

public class MongoSession : ISession
    {
        private IMongo _provider;
        public IMongoDatabase DB { get { return this._provider.Database; } }

        public MongoSession()
        {
            //this looks for a connection string in your Web.config
            _provider = Mongo.Create("db");
        }

        public void CommitChanges()
        {
            //mongo isn't transactional in this way
        }

        public void Delete(System.Linq.Expressions.Expression<Func> expression) where T : class, new()
        {
            var items = All().Where(expression);
            foreach (T item in items)
            {
                Delete(item);
            }
        }

        public void Delete(T item) where T : class, new()
        {
            DB.GetCollection().Delete(item);
        }

        public void DeleteAll() where T : class, new()
        {
            DB.DropCollection(typeof(T).Name);
        }

        public T Single(System.Linq.Expressions.Expression<Func> expression) where T : class, new()
        {
            return All().Where(expression).SingleOrDefault();
        }

        public IQueryable All() where T : class, new()
        {
            return _provider.GetCollection().AsQueryable();
        }

        public void Add(T item) where T : class, new()
        {
            DB.GetCollection().Insert(item);
        }

        public void Add(IEnumerable items) where T : class, new()
        {
            foreach (T item in items)
            {
                Add(item);
            }
        }

        public void Update(T item) where T : class, new()
        {
            DB.GetCollection().UpdateOne(item, item);
        }

        //Helper for using map reduce in mongo
        public T MapReduce(string map, string reduce)
        {
            T result = default(T);
            MapReduce mr = DB.CreateMapReduce();

            MapReduceResponse response =
                mr.Execute(new MapReduceOptions(typeof(T).Name)
                {
                    Map = map,
                    Reduce = reduce
                });
            IMongoCollection<MapReduceResult> coll = response.GetCollection<MapReduceResult>();
            MapReduceResult r = coll.Find().FirstOrDefault();
            result = r.Value;

            return result;
        }

        public void Dispose()
        {
            _provider.Dispose();
        }
    }

